import React, { useState } from 'react';
import { CssBaseline, ThemeProvider, createTheme } from '@mui/material';
import FactForm from './components/FactForm';
import FactResult from './components/FactResult';
import { FactRequest, FactResponse } from './types';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [fact, setFact] = useState<FactResponse | null>(null);

  const handleSubmit = async (request: FactRequest) => {
    setIsLoading(true);
    try {
      // TODO: Implementovat volání API
      // Pro demonstraci použijeme mock data
      const mockResponse: FactResponse = {
        title: 'Vznik Československa',
        description: '28. října 1918 bylo vyhlášeno samostatné Československo. Tento den se stal státním svátkem a symbolizuje vznik samostatného československého státu po rozpadu Rakouska-Uherska.',
        imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/4c/Proclamation_of_Czechoslovak_Independence_in_1918.jpg/1200px-Proclamation_of_Czechoslovak_Independence_in_1918.jpg',
        date: '28. října 1918'
      };
      
      setFact(mockResponse);
    } catch (error) {
      console.error('Chyba při generování faktu:', error);
      // TODO: Přidat zobrazení chyby uživateli
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div className="App">
        <FactForm onSubmit={handleSubmit} isLoading={isLoading} />
        {fact && <FactResult fact={fact} />}
      </div>
    </ThemeProvider>
  );
}

export default App;
